custom_post_processor_command:
curl -u {{user `artifactory_user`}}:{{user `artifactory_key`}} -T output/{{user `build_version`}}/{{user `build_version`}}.ova "https://artifactory.devops.telekom.de/artifactory/schiff-generic/ovas/ubuntu/{{user `build_version`}}.ova;CI_COMMIT_REF_SLUG={{user `CI_COMMIT_REF_SLUG`}};os_display_name={{user `os_display_name` | replace_all " " "-" }};KUBERNETES_VERSION={{user `kubernetes_semver`}};CONTAINERD_VERSION={{user `containerd_version`}}" && echo "BUILD_VERSION={{user `build_version`}}
KUBERNETES_VERSION={{user `kubernetes_semver`}}
CONTAINERD_VERSION={{user `containerd_version`}}" >> "../../build.env"
