custom_post_processor_command:
curl -u {{user `artifactory_user`}}:{{user `artifactory_key`}} -T output/{{user `build_name`}}-kube-{{user `kubernetes_semver`}}/{{user `build_name`}}-kube-{{user `kubernetes_semver`}} "https://artifactory.devops.telekom.de/artifactory/schiff-generic/qemu-images/ubuntu/{{user `build_name`}}-kube-{{user `kubernetes_semver`}}.qcow2;CI_COMMIT_REF_SLUG={{user `CI_COMMIT_REF_SLUG`}};os_display_name={{user `os_display_name` | replace_all " " "-" }};KUBERNETES_VERSION={{user `kubernetes_semver`}};CONTAINERD_VERSION={{user `containerd_version`}}" && qemu-img convert -O qcow2 -c output/{{user `build_name`}}-kube-{{user `kubernetes_semver`}}/{{user `build_name`}}-kube-{{user `kubernetes_semver`}} output/{{user `build_name`}}-kube-{{user `kubernetes_semver`}}/{{user `build_name`}}-kube-{{user `kubernetes_semver`}}-compressed && curl -u {{user `artifactory_user`}}:{{user `artifactory_key`}} -T output/{{user `build_name`}}-kube-{{user `kubernetes_semver`}}/{{user `build_name`}}-kube-{{user `kubernetes_semver`}}-compressed "https://artifactory.devops.telekom.de/artifactory/schiff-generic/qemu-images/ubuntu/{{user `build_name`}}-kube-{{user `kubernetes_semver`}}-compressed.qcow2;CI_COMMIT_REF_SLUG={{user `CI_COMMIT_REF_SLUG`}};os_display_name={{user `os_display_name` | replace_all " " "-" }};KUBERNETES_VERSION={{user `kubernetes_semver`}};CONTAINERD_VERSION={{user `containerd_version`}}" && echo "BUILD_VERSION={{user `build_version`}}
KUBERNETES_VERSION={{user `kubernetes_semver`}}
CONTAINERD_VERSION={{user `containerd_version`}}" >> "../../build.env"
